/* eslint-disable react/forbid-prop-types */
/* eslint-disable jsx-a11y/click-events-have-key-events */
import styled from 'styled-components'
import React, { Component } from 'react'
import PropTypes from 'prop-types'
import FlagDocument from './FlagDocument'
import { IconLang } from './Icons'

class PopupChangeLanguage extends Component {

  state = {
    showDropDown: false
  }

  componentDidMount() {
    document.addEventListener('mousedown', this.handleClickOutside)
  }

  componentWillUnmount() {
    document.removeEventListener('mousedown', this.handleClickOutside)
  }

  showDropDownUserGroup = () => {
    const { showDropDown } = this.state
    this.setState({
      showDropDown: !showDropDown
    })
  }

  handleClickOutside = event => {
    if (this.wrapperRef && !this.wrapperRef.contains(event.target)) {
      this.setState({
        showDropDown: false
      })
    }
  }

  setWrapperRef = node => {
    this.wrapperRef = node
  }

  render() {
    const { children } = this.props
    const { showDropDown } = this.state
    return (
      <React.Fragment>
      <Language onClick= {()=> { this.showDropDownUserGroup()}}> 
        <IconLang />
      </Language>
      <Wrapper>
        <div className="row-country">
          <div className="icon-flag">
            <FlagDocument lang={'en'} />
          </div>
          <div className="name">English</div>
        </div>
        <div className="row-country">
          <div className="icon-flag">
            <FlagDocument lang={'en'} />
          </div>
          <div className="name">English</div>
        </div>
        <div className="row-country">
          <div className="icon-flag">
            <FlagDocument lang={'en'} />
          </div>
          <div className="name">English</div>
        </div>
        <div className="row-country">
          <div className="icon-flag">
            <FlagDocument lang={'en'} />
          </div>
          <div className="name">English</div>
        </div>
     </Wrapper>
     </React.Fragment>

    )
  }
}

PopupChangeLanguage.propTypes = {

}
const Language = styled.div`
  flex: 1 1 auto;
  height: 60px;
  display: flex;
  flex-direction: column;
  width: 40px;
  max-width: 40px;
  min-width: 40px;
  justify-content: flex-start;
  align-items: flex-start;
  padding-top: 12px;
  box-sizing: border-box;

  img {
    cursor: pointer;
    width: 16px;
    object-fit: contain;
  }
`
const Wrapper = styled.div`
  position: absolute;
  right: -150px;
  width: 150px;
  z-index: 1000;
  background-color: ${props => props.theme.colorContent};
  top: -1px;
  box-sizing: border-box;
  border: 1px solid ${props => props.theme.colorBlue};
  .row-country {
    border-bottom: 1px solid ${props => props.theme.colorBlue};
    height: 60px;
    box-sizing: border-box;
    display: flex;
    align-items: center;
    padding-left: 8px;
    .icon-flag {
      width: 60px;
      flex: 1 1 auto;
    }
    svg {
      width: 40px;
    }
    &:last-child {
      border-bottom: 1px solid transparent;
    }
    .name {
      flex: 1 1 100%;
      font-family: ${props => props.theme.bodyFont};
      color: ${props => props.theme.colorTextBody};
    }
  }
`

export default PopupChangeLanguage
